import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, Mail } from 'lucide-react';
import axios from 'axios';

const questions = [
  {
    id: 'memory',
    question: 'Welche Momente sollen euch noch Jahre später ein Lächeln ins Gesicht zaubern?',
    options: [
      { value: 'breakfast', label: 'Wir frühstücken zu zweit im Bett, draußen dämmert langsam der Tag.' },
      { value: 'restaurant', label: 'Wir sitzen in einem kleinen Restaurant und genießen das beste Gericht unseres Lebens.' },
      { value: 'friends', label: 'Die stundenlangen gespräche bei gutem Wein mit guten Freunden.' },
      { value: 'landscape', label: 'Wir stehen vor einer unglaublichen Kulisse und halten diesen Moment für immer fest.' },
      { value: 'adventure', label: 'Ein Abenteuer, von dem wir später allen erzählen: kaum zu glauben, dass wir das gemacht haben.' },
      { value: 'cooking', label: 'Wir lernen von einer Nonna ihr geheimstes Rezept und kochen es gemeinsam nach.' },
      { value: 'dancing', label: 'Wir tanzen die ganze Nacht in einer Strandbar, spüren die Musik und das Meer.' }
    ]
  },
  {
    id: 'setting',
    question: 'Wo verbringt ihr eure Lieblingsmomente am liebsten?',
    options: [
      { value: 'nature', label: 'Wir sind völlig abgeschottet in der Natur, nur wir und die frische Luft.' },
      { value: 'city', label: 'Wir sind mitten im Geschehen, umgeben von Menschen, Cafés und Stadtleben.' },
      { value: 'pool', label: 'Wir genießen einen Pool und die Aussicht, die uns einfach den Atem raubt.' },
      { value: 'mountains', label: 'Wir stehen in der Nähe von Bergen, spüren die klare Luft und die Weite um uns herum.' },
      { value: 'lake', label: 'Wir sitzen am See, hören das Wasser plätschern und fühlen uns eins mit der Umgebung.' }
    ]
  },
  {
    id: 'luxury',
    question: 'Was macht ein Wochenende für euch besonders?',
    options: [
      { value: 'service', label: 'Wir lassen uns rundum verwöhnen: Zimmerservice, Spa, alles da – wir müssen nichts tun.' },
      { value: 'unique', label: 'Wir erleben etwas Einmaliges: unter freiem Sternenhimmel schlafen, ein Tier beobachten, eine besondere Herausforderung.' },
      { value: 'exclusive', label: 'Wir sind an einem Ort, den nur wenige kennen, entdecken Geheimtipps und spüren die Exklusivität.' },
      { value: 'time', label: 'Wir brauchen kein Programm, wir genießen einfach ungestörte Zeit miteinander, reden, lachen, nichts muss passieren.' }
    ]
  }
];

const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [email, setEmail] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));

    if (currentQuestion < questions.length - 1) {
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    } else {
      // After the last question, move to email form
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    }
  };

  const goBack = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await axios.post('http://localhost:5001/api/quiz', {
        answers,
        email
      });
      setIsSubmitted(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Es gab einen Fehler beim Absenden. Bitte versuchen Sie es erneut.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetQuiz = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setEmail('');
    setIsSubmitted(false);
  };

  if (isSubmitted) {
    return (
      <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
        <div className="container-editorial max-w-4xl text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="w-24 h-24 bg-sage-100 rounded-none flex items-center justify-center mx-auto mb-8">
              <CheckCircle className="w-12 h-12 text-sage-600" />
            </div>
            <h2 className="text-section mb-6 text-editorial-900">
              Danke! Wir schicken euch schon bald die besten Kurztrips für euer Gefühl.
            </h2>
            <p className="text-body mb-8 max-w-2xl mx-auto text-editorial-700">
              Ihr werdet eine E-Mail mit euren personalisierten Reiseempfehlungen erhalten.
            </p>
            <button 
              onClick={resetQuiz}
              className="btn-secondary"
            >
              Quiz erneut starten
            </button>
          </motion.div>
        </div>
      </section>
    );
  }

  return (
    <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
      <div className="container-editorial max-w-4xl">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="section-badge mb-6">
            <div className="section-badge-dot"></div>
            <p className="text-caption">Unser Gespür</p>
          </div>
          <h2 className="text-section mb-6 text-editorial-900">
            Wir wissen, was euch glücklich macht.
          </h2>
          <p className="text-body max-w-2xl mx-auto text-editorial-700 mb-8">
            In 3 Minuten zu eurem perfekten Wochenende. Wir sind die Münchner, die jeden versteckten Spot im Umkreis von 400km kennen. Die wissen, welches Hotel den besten Sonnenuntergang hat. Und welcher Wirt euch das Gefühl gibt, endlich angekommen zu sein.<br/><br/>
            Macht unser Quiz – dann schicken wir euch Trips, bei denen ihr schon beim Lesen packen wollt.
          </p>
        </motion.div>

        <div className="card p-12">
          {/* Progress bar */}
          <div className="mb-12">
            <div className="flex justify-between text-caption text-editorial-500 mb-4">
              <span>Frage {currentQuestion + 1} von {questions.length}</span>
              <span>{Math.round(((currentQuestion + 1) / questions.length) * 100)}%</span>
            </div>
            <div className="w-full bg-sage-200 rounded-none h-1">
              <motion.div 
                className="bg-coral-500 h-1 rounded-none"
                initial={{ width: 0 }}
                animate={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                transition={{ duration: 0.3 }}
              />
            </div>
          </div>

          <AnimatePresence mode="wait">
            {currentQuestion < questions.length ? (
              <motion.div
                key={currentQuestion}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                {currentQuestion > 0 && (
                  <div className="mb-8">
                    <button
                      onClick={goBack}
                      className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                      </svg>
                      Zurück
                    </button>
                  </div>
                )}

                <h3 className="text-2xl font-medium mb-8 text-left text-editorial-900">
                  {questions[currentQuestion].question}
                </h3>

                <div className="space-y-4">
                  {questions[currentQuestion].options.map((option, index) => (
                    <motion.button
                      key={option.value}
                      className="w-full p-5 text-left border-2 border-sage-200 rounded-lg hover:border-coral-400 hover:bg-coral-50 transition-all duration-300 group"
                      onClick={() => handleAnswer(questions[currentQuestion].id, option.value)}
                    >
                      <span className="text-base leading-relaxed text-editorial-700 group-hover:text-editorial-900 transition-colors duration-300">
                        {option.label}
                      </span>
                    </motion.button>
                  ))}
                </div>
              </motion.div>
            ) : (
              <motion.div
                key="email-form"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                <div className="mb-8">
                  <button
                    onClick={goBack}
                    className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                  >
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                    Zurück zu den Fragen
                  </button>
                </div>

                <div className="text-center mb-12">
                  <div className="w-20 h-20 bg-warm-100 rounded-none flex items-center justify-center mx-auto mb-8">
                    <Mail className="w-10 h-10 text-editorial-700" />
                  </div>
                  <h3 className="text-2xl font-medium mb-6 text-editorial-900">
                    Eure drei perfekten Auszeiten warten bereits.
                  </h3>
                  <p className="text-body max-w-2xl mx-auto text-editorial-700">
                    Basierend auf euren Antworten haben wir handverlesene Erlebnisse kuratiert. Tragt eure E-Mail ein und wir schicken sie euch – mit allem, was ihr wissen müsst.
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="mb-8">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Eure E-Mail-Adresse"
                      className="w-full p-6 border border-editorial-300 rounded-none focus:outline-none focus:ring-2 focus:ring-editorial-500 focus:border-transparent text-body"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="w-full btn-primary text-lg py-6 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isSubmitting ? 'Wird gesendet...' : 'Trips senden'}
                  </button>
                </form>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default Quiz;
