import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, Mail } from 'lucide-react';
import axios from 'axios';

const questions = [
  {
    id: 'memory',
    question: 'üåü Was wird euer "Wei√üt du noch...?"-Moment?',
    options: [
      { value: 'romance', label: 'üíï Romance Escapes\nChampagner-Fr√ºhst√ºck im Bett bis die Sonne untergeht' },
      { value: 'foodie', label: 'üçæ Foodie Adventures\nDas 7-G√§nge-Men√º bei Tim Raue, das euer Leben ver√§ndert' },
      { value: 'squad', label: 'üëØ‚Äç‚ôÄÔ∏è Squad Goals\nBis 4 Uhr morgens lachen, bis der Bauch wehtut und die Nachbarn klopfen' },
      { value: 'adrenaline', label: '‚õ∞Ô∏è Adrenaline Rush\nDen Gipfel erobern und die Welt zu euren F√º√üen liegen sehen' },
      { value: 'decadence', label: 'ü•Ç Pure Decadence\nRos√© auf der Yacht oder Champagner im Infinity Pool' },
      { value: 'cultural', label: 'üáÆüáπ Cultural Immersion\nNonnas geheime Pasta-Kunst lernen (und f√ºr immer behalten)' },
      { value: 'wild', label: 'üåä Wild Nights\nBarfu√ü im Sand tanzen, w√§hrend die Sonne aufgeht' },
      { value: 'insider', label: 'üèõÔ∏è Insider Access\nMit dem besten Architekten der Stadt durch Geheimg√§nge schleichen' },
      { value: 'culture', label: 'üé® Mind-Blowing Culture\nVor DIESEM Kunstwerk stehen, das alles ver√§ndert' }
    ]
  },
  {
    id: 'setting',
    question: 'üè® Euer Zuhause auf Zeit?',
    options: [
      { value: 'luxury', label: 'üëë The Ultimate Luxury\nPersonal Butler, goldene Wasserh√§hne, jeder Wunsch wird erf√ºllt bevor ihr ihn ausgesprochen habt' },
      { value: 'authentic', label: 'üèöÔ∏è Authentic Soul\nJahrhundertealte Mauern, Geschichten in jeder Ecke, der Besitzer erz√§hlt beim Wein von seinem Urgro√üvater' },
      { value: 'instagram', label: 'üì∏ Instagram Heaven\nRooftop Pool mit Skyline-View, Designer-M√∂bel, jede Ecke ein perfektes Foto' },
      { value: 'hidden', label: 'üèîÔ∏è Hidden Gem\nNur per Helikopter erreichbar, kein Handynetz, nur ihr und die Natur' }
    ]
  },
  {
    id: 'time_allocation',
    question: '‚è∞ Wie verbringt ihr eure kostbare Zeit?\nVerteilt 100% auf die Dinge, die euch wirklich wichtig sind:',
    isPercentageAllocation: true,
    options: [
      { value: 'culinary', label: 'ü•ò Culinary Highs\nMichelin-Sterne, Streetfood-Touren, Kochkurse mit Locals' },
      { value: 'suite', label: 'üõèÔ∏è Suite Life\nRoom Service, Balkon mit View, einfach mal nichts tun m√ºssen' },
      { value: 'culture', label: 'üé≠ VIP Culture\nPrivate Museumsf√ºhrungen, Backstage-Access, Einheimischen-Geheimtipps' },
      { value: 'active', label: 'üèÉ‚Äç‚ôÄÔ∏è Active Adventures\nSunrise Yoga, Epic Hikes, E-Bike durch Weinberge' },
      { value: 'wellness', label: 'üíÜ‚Äç‚ôÄÔ∏è Wellness Therapy\nSpa-Days, Hot Stone Massages, Digital Detox' },
      { value: 'nightlife', label: 'üç∏ Nightlife Legends\nRooftop-Bars, Underground-Clubs, bis die Sonne aufgeht' },
      { value: 'surprise', label: 'üéÅ Plot Twists\nLasst uns √ºberraschen - die besten Geschichten entstehen ungeplant' },
      { value: 'wandering', label: 'üö∂‚Äç‚ôÄÔ∏è Urban Wandering\nDurch Gassen schlendern, spontan in Caf√©s verschwinden, die Stadt auf euch wirken lassen' }
    ]
  }
];

const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [email, setEmail] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [percentageAllocations, setPercentageAllocations] = useState({});

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));

    if (currentQuestion < questions.length - 1) {
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    } else {
      // After the last question, move to email form
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    }
  };

  const handlePercentageChange = (optionValue, value) => {
    const numValue = parseInt(value) || 0;
    setPercentageAllocations(prev => ({
      ...prev,
      [optionValue]: numValue
    }));
  };

  const getTotalPercentage = () => {
    return Object.values(percentageAllocations).reduce((sum, val) => sum + val, 0);
  };

  const canProceedFromPercentageQuestion = () => {
    return getTotalPercentage() === 100;
  };

  const goBack = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await axios.post('http://localhost:5001/api/quiz', {
        answers,
        percentageAllocations,
        email
      });
      setIsSubmitted(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Es gab einen Fehler beim Absenden. Bitte versuchen Sie es erneut.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetQuiz = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setEmail('');
    setIsSubmitted(false);
    setPercentageAllocations({});
  };

  if (isSubmitted) {
    return (
      <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
        <div className="container-editorial max-w-4xl text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="w-24 h-24 bg-sage-100 rounded-none flex items-center justify-center mx-auto mb-8">
              <CheckCircle className="w-12 h-12 text-sage-600" />
            </div>
            <h2 className="text-section mb-6 text-editorial-900">
              Danke! Wir schicken euch schon bald die besten Kurztrips f√ºr euer Gef√ºhl.
            </h2>
            <p className="text-body mb-8 max-w-2xl mx-auto text-editorial-700">
              Ihr werdet eine E-Mail mit euren personalisierten Reiseempfehlungen erhalten.
            </p>
            <button 
              onClick={resetQuiz}
              className="btn-secondary"
            >
              Quiz erneut starten
            </button>
          </motion.div>
        </div>
      </section>
    );
  }

  return (
    <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
      <div className="container-editorial max-w-4xl">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="section-badge mb-6">
            <div className="section-badge-dot"></div>
            <p className="text-caption">Unser Gesp√ºr</p>
          </div>
          <h2 className="text-section mb-6 text-editorial-900">
            Euer perfekter Lifestyle-Trip wartet
          </h2>
          <p className="text-body max-w-2xl mx-auto text-editorial-700 mb-8">
            Nach Jahren im Gesch√§ft wissen wir: Es braucht nur 3 richtige Fragen, um euren Traumtrip zu finden. W√§hrend andere euch 50 Filter vorsetzen, haben wir es auf das Wesentliche reduziert. Quiz ausf√ºllen, perfekte Empfehlung bekommen ‚Äì so einfach ist das.
          </p>
        </motion.div>

        <div className="card p-12">
          {/* Progress bar */}
          <div className="mb-12">
            <div className="flex justify-between text-caption text-editorial-500 mb-4">
              <span>Frage {currentQuestion + 1} von {questions.length}</span>
              <span>{Math.round(((currentQuestion + 1) / questions.length) * 100)}%</span>
            </div>
            <div className="w-full bg-sage-200 rounded-none h-1">
              <motion.div 
                className="bg-coral-500 h-1 rounded-none"
                initial={{ width: 0 }}
                animate={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                transition={{ duration: 0.3 }}
              />
            </div>
          </div>

          <AnimatePresence mode="wait">
            {currentQuestion < questions.length ? (
              <motion.div
                key={currentQuestion}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                {currentQuestion > 0 && (
                  <div className="mb-8">
                    <button
                      onClick={goBack}
                      className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                      </svg>
                      Zur√ºck
                    </button>
                  </div>
                )}

                <h3 className="text-2xl font-medium mb-8 text-left text-editorial-900 whitespace-pre-line">
                  {questions[currentQuestion].question}
                </h3>

                {questions[currentQuestion].isPercentageAllocation ? (
                  <div className="space-y-6">
                    {questions[currentQuestion].options.map((option, index) => (
                      <div key={option.value} className="border-2 border-sage-200 rounded-lg p-6 hover:border-coral-400 transition-all duration-300">
                        <div className="flex items-center justify-between mb-4">
                          <div className="flex-1">
                            <h4 className="text-lg font-medium text-editorial-900 whitespace-pre-line">
                              {option.label}
                            </h4>
                          </div>
                          <div className="flex items-center space-x-2 ml-4">
                            <input
                              type="number"
                              min="0"
                              max="100"
                              value={percentageAllocations[option.value] || 0}
                              onChange={(e) => handlePercentageChange(option.value, e.target.value)}
                              className="w-20 p-2 border border-editorial-300 rounded text-center focus:outline-none focus:ring-2 focus:ring-coral-400"
                            />
                            <span className="text-editorial-600">%</span>
                          </div>
                        </div>
                        <div className="w-full bg-sage-200 rounded-full h-2">
                          <div 
                            className="bg-coral-500 h-2 rounded-full transition-all duration-300"
                            style={{ width: `${percentageAllocations[option.value] || 0}%` }}
                          />
                        </div>
                      </div>
                    ))}

                    <div className="mt-8 p-4 bg-sage-50 rounded-lg">
                      <div className="flex items-center justify-between">
                        <span className="text-editorial-700 font-medium">
                          Gesamt: {getTotalPercentage()}%
                        </span>
                        {getTotalPercentage() !== 100 && (
                          <span className="text-coral-600 text-sm">
                            {getTotalPercentage() < 100 ? `Noch ${100 - getTotalPercentage()}% zu verteilen` : `${getTotalPercentage() - 100}% zu viel`}
                          </span>
                        )}
                      </div>
                    </div>

                    <button
                      onClick={() => {
                        if (canProceedFromPercentageQuestion()) {
                          setAnswers(prev => ({ ...prev, [questions[currentQuestion].id]: percentageAllocations }));
                          setTimeout(() => {
                            setCurrentQuestion(prev => prev + 1);
                          }, 300);
                        }
                      }}
                      disabled={!canProceedFromPercentageQuestion()}
                      className="w-full btn-primary text-lg py-6 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Weiter
                    </button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {questions[currentQuestion].options.map((option, index) => (
                      <motion.button
                        key={option.value}
                        className="w-full p-6 text-left border-2 border-sage-200 rounded-lg hover:border-coral-400 hover:bg-coral-50 transition-all duration-300 group"
                        onClick={() => handleAnswer(questions[currentQuestion].id, option.value)}
                      >
                        <span className="text-base leading-relaxed text-editorial-700 group-hover:text-editorial-900 transition-colors duration-300 whitespace-pre-line">
                          {option.label}
                        </span>
                      </motion.button>
                    ))}
                  </div>
                )}
              </motion.div>
            ) : (
              <motion.div
                key="email-form"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                <div className="mb-8">
                  <button
                    onClick={goBack}
                    className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                  >
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                    Zur√ºck zu den Fragen
                  </button>
                </div>

                <div className="text-center mb-12">
                  <div className="w-20 h-20 bg-warm-100 rounded-none flex items-center justify-center mx-auto mb-8">
                    <Mail className="w-10 h-10 text-editorial-700" />
                  </div>
                  <h3 className="text-2xl font-medium mb-6 text-editorial-900">
                    Eure drei perfekten Auszeiten warten bereits.
                  </h3>
                  <p className="text-body max-w-2xl mx-auto text-editorial-700">
                    Basierend auf euren Antworten haben wir handverlesene Erlebnisse kuratiert. Tragt eure E-Mail ein und wir schicken sie euch ‚Äì mit allem, was ihr wissen m√ºsst.
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="mb-8">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Eure E-Mail-Adresse"
                      className="w-full p-6 border border-editorial-300 rounded-none focus:outline-none focus:ring-2 focus:ring-editorial-500 focus:border-transparent text-body"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="w-full btn-primary text-lg py-6 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isSubmitting ? 'Wird gesendet...' : 'Trips senden'}
                  </button>
                </form>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default Quiz;
