const ExperienceCard = ({ experience, index, isExpanded, onToggle }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showReservationForm, setShowReservationForm] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [reservationData, setReservationData] = useState({
    firstName: '',
    lastName: '',
    arrival: '',
    departure: '',
    guests: 2,
    wishes: '',
    email: '',
    phone: '',
    termsAccepted: false,
    marketingAccepted: false
  });

  const prevImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => 
      prev === 0 ? experience.images.length - 1 : prev - 1
    );
  };

  const nextImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => 
      prev === experience.images.length - 1 ? 0 : prev + 1
    );
  };

  const goToImage = (e, index) => {
    e.stopPropagation();
    setCurrentImageIndex(index);
  };

  const handleReservationSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/reservation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          experience: experience.title,
          ...reservationData
        }),
      });

      if (response.ok) {
        setShowSuccessModal(true);
        setShowReservationForm(false);
        setReservationData({
          firstName: '',
          lastName: '',
          arrival: '',
          departure: '',
          guests: 2,
          wishes: '',
          email: '',
          phone: '',
          termsAccepted: false,
          marketingAccepted: false
        });
      } else {
        throw new Error('Reservierung fehlgeschlagen');
      }
    } catch (error) {
      console.error('Error submitting reservation:', error);
      alert('Es gab einen Fehler bei der Reservierung. Bitte versuchen Sie es erneut.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setReservationData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <motion.div
      className="group cursor-pointer"
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, delay: index * 0.1 }}
      viewport={{ once: true }}
      onClick={() => onToggle(experience.id)}
    >
      {/* Creative Image Layout */}
      <div className="relative mb-8">
        <div className="relative aspect-square image-creative">
          <img 
            src={experience.image} 
            alt={experience.caption}
            className="w-full h-full object-cover"
          />

          {/* Subtle overlay */}
          <div className="absolute inset-0 bg-gradient-to-t from-black/10 via-transparent to-transparent" />

          {/* "Hierhin gehen" Badge */}
          <div className="absolute top-4 right-4 bg-coral-500 text-white rounded-full px-4 py-2 shadow-lg">
            <span className="font-semibold text-sm">Hierhin gehen</span>
          </div>
        </div>

        {/* Creative accent line */}
        <div className="accent-line"></div>
      </div>

      {/* Content */}
      <div className="space-y-4">
        {/* Poetic Caption */}
        <p className="text-lg leading-relaxed text-sage-600 font-medium italic mb-2">
          {experience.caption}
        </p>

        {/* Title */}
        <div className="flex items-start justify-between">
          <h3 className="text-2xl font-semibold text-gray-900 leading-tight flex-1">
            {experience.title}
          </h3>
          {experience.septemberOffer && (
            <div className="ml-3 flex-shrink-0">
              <div className="relative">
                {/* September Offer Badge */}
                <div className="bg-gradient-to-r from-sage-500 to-sage-600 text-white px-3 py-1.5 rounded-full shadow-lg transform rotate-3 hover:rotate-0 transition-transform duration-300">
                  <div className="flex items-center space-x-1">
                    <svg 
                      width="14" 
                      height="14" 
                      viewBox="0 0 24 24" 
                      fill="currentColor"
                      className="text-sage-100"
                    >
                      <path d="M12 2L15.09 8.26L22 9L17 14L18.18 21L12 17.77L5.82 21L7 14L2 9L8.91 8.26L12 2Z"/>
                    </svg>
                    <span className="text-xs font-bold tracking-wide">SEPT</span>
                  </div>
                </div>
                {/* Subtle glow effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-sage-400 to-sage-500 rounded-full blur-sm opacity-30 -z-10"></div>
              </div>
            </div>
          )}
        </div>

        {/* Travel Time */}
        <div className="flex items-center space-x-2 text-sm text-sage-600 font-medium">
          <Clock className="w-4 h-4" />
          <span>{experience.travelTime}</span>
        </div>

        {/* Expandable Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.4 }}
              className="overflow-hidden"
            >
              <div className="space-y-6 pt-6 border-t border-gray-200">
                {/* Description */}
                <div>
                  <p className="text-base text-gray-700 leading-relaxed">{experience.description}</p>
                </div>

                {/* Image Gallery */}
                <div className="space-y-6">
                  <div className="relative aspect-[3/2] overflow-hidden rounded-lg bg-warm-100">
                    <motion.img 
                      key={currentImageIndex}
                      src={experience.images[currentImageIndex]} 
                      alt={`${experience.title} ${currentImageIndex + 1}`}
                      className="w-full h-full object-cover"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    />

                    {/* Navigation arrows */}
                    {experience.images.length > 1 && (
                      <>
                        <button 
                          onClick={prevImage}
                          className="absolute left-3 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm rounded-full p-2 shadow-lg hover:bg-white transition-colors"
                        >
                          <ChevronLeft className="w-4 h-4 text-gray-700" />
                        </button>
                        <button 
                          onClick={nextImage}
                          className="absolute right-3 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm rounded-full p-2 shadow-lg hover:bg-white transition-colors"
                        >
                          <ChevronRight className="w-4 h-4 text-gray-700" />
                        </button>
                      </>
                    )}
                  </div>

                  {/* Image dots/thumbnails */}
                  {experience.images.length > 1 && (
                    <div className="flex justify-center space-x-3">
                      {experience.images.map((img, index) => (
                        <button
                          key={index}
                          onClick={(e) => goToImage(e, index)}
                          className={`relative overflow-hidden rounded transition-all duration-200 ${
                            currentImageIndex === index 
                              ? 'ring-2 ring-gray-800 w-16 h-10' 
                              : 'w-12 h-8 opacity-60 hover:opacity-80'
                          }`}
                        >
                          <img 
                            src={img} 
                            alt={`Thumbnail ${index + 1}`}
                            className="w-full h-full object-cover"
                          />
                        </button>
                      ))}
                    </div>
                  )}
                </div>

                {/* Quick Info */}
                <div className="grid grid-cols-2 gap-4 text-center">
                  <div className="space-y-1">
                    <Clock className="w-4 h-4 text-gray-500 mx-auto" />
                    <span className="text-base text-gray-600">{experience.duration}</span>
                  </div>
                  <div className="space-y-1">
                    <Users className="w-4 h-4 text-gray-500 mx-auto" />
                    <span className="text-base text-gray-600">{experience.groupSize}</span>
                  </div>
                </div>

                {/* Highlights */}
                <div>
                  <h4 className="text-base font-medium text-gray-900 mb-3">Was dich erwartet</h4>
                  <ul className="space-y-2">
                    {experience.details.map((detail, index) => (
                      <li key={index} className="flex items-start space-x-3">
                        <div className="w-1 h-1 bg-gray-400 rounded-full mt-2 flex-shrink-0" />
                        <span className="text-base text-gray-700">{detail}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Duration & Group Size */}
                <div className="pt-4 border-t border-gray-100">
                  <div className="text-center mb-4">
                    <p className="text-sm text-gray-600">{experience.duration} • {experience.groupSize}</p>
                  </div>

                  {/* Testimonial */}
                  {experience.testimonial && (
                    <div className="bg-warm-50 p-4 rounded-lg mb-4">
                      <p className="text-sm italic text-gray-700 text-center">"{experience.testimonial}"</p>
                    </div>
                  )}

                  {/* Price - Positioned after value is established */}
                  <div className={`rounded-lg p-4 mb-4 ${experience.septemberOffer ? 'bg-gradient-to-br from-coral-50 to-coral-100 border border-coral-200' : 'bg-sage-50'}`}>
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="text-xl font-semibold text-gray-900">
                          {experience.price}
                        </div>
                        {experience.originalPrice && (
                          <div className="text-lg text-gray-400 line-through">
                            {experience.originalPrice}
                          </div>
                        )}
                      </div>
                      <div className="text-right">
                        <div className="text-sm text-sage-600 font-medium">Alles inklusive</div>
                        {experience.originalPrice && (
                          <div className="text-xs text-gray-500">Wert: {experience.originalPrice}</div>
                        )}
                      </div>
                    </div>
                    {experience.septemberOffer && (
                      <div className="mt-3 pt-3 border-t border-coral-200">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            <div className="bg-gradient-to-r from-coral-500 to-coral-600 text-white px-3 py-1.5 rounded-full text-xs font-bold shadow-md">
                              SEPTEMBER ANGEBOT
                            </div>
                            <div className="text-sm text-coral-700 font-semibold">
                              Exklusiv für September
                            </div>
                          </div>
                          <div className="text-xs text-coral-600 font-medium">
                            Begrenzte Verfügbarkeit
                          </div>
                        </div>
                        <div className="mt-2 text-xs text-coral-700 italic">
                          ✨ Diese außergewöhnlichen Erlebnisse sind normalerweise nicht zu diesem Preis verfügbar
                        </div>
                      </div>
                    )}
                  </div>

                  <button 
                    onClick={(e) => {
                      e.stopPropagation();
                      setShowReservationForm(true);
                    }}
                    className="w-full btn-primary text-base px-6 py-3"
                  >
                    Jetzt reservieren
                  </button>
                </div>

                {/* Collapse indicator */}
                <div className="flex items-center justify-center pt-2">
                  <ChevronUp className="w-4 h-4 text-gray-400" />
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Reservation Form Modal */}
        <AnimatePresence>
          {showReservationForm && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4"
              onClick={() => setShowReservationForm(false)}
            >
              <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.9, opacity: 0 }}
                className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto"
                onClick={(e) => e.stopPropagation()}
              >
                {/* Header */}
                <div className="flex items-center justify-between p-6 border-b border-gray-200">
                  <div>
                    <h3 className="text-xl font-medium text-gray-900">Reservierung anfragen</h3>
                    <p className="text-sm text-gray-600 mt-1">{experience.title}</p>
                  </div>
                  <button
                    onClick={() => setShowReservationForm(false)}
                    className="text-gray-400 hover:text-gray-600 transition-colors"
                  >
                    <X className="w-6 h-6" />
                  </button>
                </div>

                {/* Special Offer Message */}
                {experience.septemberOffer && (
                  <div className="px-6 pt-4 pb-2">
                    <div className="bg-gradient-to-r from-coral-50 to-coral-100 border border-coral-200 rounded-lg p-4">
                      <div className="flex items-center space-x-3 mb-3">
                        <div className="bg-gradient-to-r from-coral-500 to-coral-600 text-white px-3 py-1.5 rounded-full text-xs font-bold shadow-md">
                          SEPTEMBER ANGEBOT
                        </div>
                        <span className="text-sm text-coral-700 font-semibold">
                          Exklusiv für September
                        </span>
                      </div>
                      <div className="space-y-2">
                        <p className="text-sm text-coral-800 font-medium">
                          Bei Buchung im September: <span className="font-bold text-lg">{experience.price}</span> statt <span className="line-through">{experience.originalPrice}</span>
                        </p>
                        <p className="text-xs text-coral-700 italic">
                          ✨ Diese außergewöhnlichen Erlebnisse sind normalerweise nicht zu diesem Preis verfügbar
                        </p>
                        <p className="text-xs text-coral-600 font-medium">
                          Begrenzte Verfügbarkeit • Nur für September-Buchungen
                        </p>
                      </div>
                    </div>
                  </div>
                )}

                {/* Form */}
                <form onSubmit={handleReservationSubmit} className="p-6 space-y-6">
                  {/* Personal Information */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <User className="w-4 h-4 inline mr-1" />
                        Vorname *
                      </label>
                      <input
                        type="text"
                        name="firstName"
                        value={reservationData.firstName}
                        onChange={handleInputChange}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <User className="w-4 h-4 inline mr-1" />
                        Nachname *
                      </label>
                      <input
                        type="text"
                        name="lastName"
                        value={reservationData.lastName}
                        onChange={handleInputChange}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                  </div>

                  {/* Contact Information */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Mail className="w-4 h-4 inline mr-1" />
                        E-Mail *
                      </label>
                      <input
                        type="email"
                        name="email"
                        value={reservationData.email}
                        onChange={handleInputChange}
                        required
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Phone className="w-4 h-4 inline mr-1" />
                        Telefonnummer
                      </label>
                      <input
                        type="tel"
                        name="phone"
                        value={reservationData.phone}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                  </div>

                  {/* Travel Dates */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Calendar className="w-4 h-4 inline mr-1" />
                        Anreise
                      </label>
                      <input
                        type="date"
                        name="arrival"
                        value={reservationData.arrival}
                        onChange={handleInputChange}
                        min={new Date().toISOString().split('T')[0]}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Calendar className="w-4 h-4 inline mr-1" />
                        Abreise
                        {experience.duration.includes('4') && (
                          <span className="text-xs text-amber-600 ml-1">(Mindestaufenthalt: 3 Nächte)</span>
                        )}
                      </label>
                      <input
                        type="date"
                        name="departure"
                        value={reservationData.departure}
                        onChange={handleInputChange}
                        min={reservationData.arrival || new Date().toISOString().split('T')[0]}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                      />
                    </div>
                  </div>

                  {/* Guests */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      <Users className="w-4 h-4 inline mr-1" />
                      Anzahl Personen
                    </label>
                    <select
                      name="guests"
                      value={reservationData.guests}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                    >
                      <option value={1}>1 Person</option>
                      <option value={2}>2 Personen</option>
                      <option value={3}>3 Personen</option>
                      <option value={4}>4 Personen</option>
                      <option value={5}>5 Personen</option>
                      <option value={6}>6 Personen</option>
                    </select>
                  </div>

                  {/* Special Wishes */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Erzählt uns von euren Träumen
                    </label>
                    <textarea
                      name="wishes"
                      value={reservationData.wishes}
                      onChange={handleInputChange}
                      rows={3}
                      placeholder="Was macht euren perfekten Trip aus? Besondere Erlebnisse, Diätwünsche, Allergien, romantische Überraschungen – wir hören zu und machen es möglich."
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-editorial-500 focus:border-editorial-500"
                    />
                  </div>

                  {/* Checkboxes */}
                  <div className="space-y-3">
                    <label className="flex items-start space-x-3">
                      <input
                        type="checkbox"
                        name="termsAccepted"
                        checked={reservationData.termsAccepted}
                        onChange={handleInputChange}
