import React from 'react';
import { motion } from 'framer-motion';

const ProblemSolution = () => {
  return (
    <section className="section-padding bg-warm-50 border-b border-warm-200">
      <div className="container-editorial text-center">
        <motion.div
          className="mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="section-badge mb-6">
            <div className="section-badge-dot"></div>
            <p className="text-caption">Unser Versprechen</p>
          </div>
        </motion.div>

        <motion.h2 
          className="text-section mb-12 text-editorial-900"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.1 }}
          viewport={{ once: true }}
        >
          Wir sind fesch unterwegs
        </motion.h2>

        <motion.div 
          className="text-body max-w-3xl mx-auto space-y-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <p>
          Wir san die Freunde, die genau wissen, wo's langgeht.
          </p>
          <p>
          Die, deren Tipps ihr nie bereut. Während andere endlos scrollen, haben wir längst die Spots getestet – die, die auf keinem Blog stehen, die man nur kennt, wenn man die richtigen Leute kennt. Wir bringen euch zu den Orten, von denen alle reden werden. Nur halt erst später.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default ProblemSolution;
