import React from 'react';
import { motion } from 'framer-motion';

const ProblemSolution = () => {
  return (
    <section className="section-padding bg-warm-50">
      <div className="container-editorial text-center">
        <motion.div
          className="mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <p className="text-caption text-editorial-600 mb-4">Unser Versprechen</p>
        </motion.div>

        <motion.h2 
          className="text-section mb-12 text-editorial-900"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.1 }}
          viewport={{ once: true }}
        >
          Das Richtige, sofort.
        </motion.h2>

        <motion.div 
          className="text-body max-w-3xl mx-auto space-y-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <p>
          Wir sind die Freunde, die immer wissen, wo's langgeht. Die, deren Empfehlungen ihr blind vertrauen könnt, weil ihr wisst – die haben den Riecher dafür. Während andere endlos scrollen, haben wir längst die Spots getestet, die wirklich funktionieren. Ihr spart euch die Zeit, damit ihr mehr Auszeit habt.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default ProblemSolution;
