const ExperienceCard = ({ experience, index, isExpanded, onToggle }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showReservationForm, setShowReservationForm] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [reservationData, setReservationData] = useState({
    firstName: '',
    lastName: '',
    arrival: '',
    departure: '',
    guests: 2,
    wishes: '',
    email: '',
    phone: '',
    termsAccepted: false,
    marketingAccepted: false
  });

  const prevImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => 
      prev === 0 ? experience.images.length - 1 : prev - 1
    );
  };

  const nextImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => 
      prev === experience.images.length - 1 ? 0 : prev + 1
    );
  };

  const goToImage = (e, index) => {
    e.stopPropagation();
    setCurrentImageIndex(index);
  };

  return (
    <motion.div
      className="group cursor-pointer"
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, delay: index * 0.1 }}
      viewport={{ once: true }}
      onClick={() => onToggle(experience.id)}
    >
      {/* Magazine-style Image with Overlay */}
      <div className="relative aspect-[4/3] overflow-hidden mb-6 rounded-lg">
        <img 
          src={experience.image} 
          alt={experience.caption}
          className="w-full h-full object-cover transition-transform duration-1000 group-hover:scale-105"
        />

        {/* Subtle overlay on hover */}
        <div className="absolute inset-0 bg-black/0 group-hover:bg-black/10 transition-colors duration-500" />

      </div>

      {/* Content */}
      <div className="space-y-4">
        {/* Poetic Caption */}
        <p className="text-lg leading-relaxed text-gray-800 font-light italic">
          {experience.caption}
        </p>

        {/* Title */}
        <h3 className="text-xl font-light text-gray-900">
          {experience.title}
        </h3>


        {/* Expandable Content */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.4 }}
              className="overflow-hidden"
            >
              <div className="space-y-6 pt-6 border-t border-gray-200">
                {/* Description */}
                <div>
                  <p className="text-base text-gray-700 leading-relaxed">{experience.description}</p>
                </div>

                {/* Image Gallery */}
                <div className="space-y-4">
                  <div className="relative aspect-[16/9] overflow-hidden rounded-lg bg-gray-100">
                    <motion.img 
                      key={currentImageIndex}
                      src={experience.images[currentImageIndex]} 
                      alt={`${experience.title} ${currentImageIndex + 1}`}
                      className="w-full h-full object-cover"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    />

                    {/* Navigation arrows */}
                    {experience.images.length > 1 && (
                      <>
                        <button 
                          onClick={prevImage}
                          className="absolute left-3 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm rounded-full p-2 shadow-lg hover:bg-white transition-colors"
                        >
                          <ChevronLeft className="w-4 h-4 text-gray-700" />
                        </button>
                        <button 
                          onClick={nextImage}
                          className="absolute right-3 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm rounded-full p-2 shadow-lg hover:bg-white transition-colors"
                        >
                          <ChevronRight className="w-4 h-4 text-gray-700" />
                        </button>
                      </>
                    )}
                  </div>

                  {/* Image dots/thumbnails */}
                  {experience.images.length > 1 && (
                    <div className="flex justify-center space-x-2">
                      {experience.images.map((img, index) => (
                        <button
                          key={index}
                          onClick={(e) => goToImage(e, index)}
                          className={`relative overflow-hidden rounded transition-all duration-200 ${
                            currentImageIndex === index 
                              ? 'ring-2 ring-gray-800 w-12 h-8' 
                              : 'w-8 h-6 opacity-60 hover:opacity-80'
                          }`}
                        >
                          <img 
                            src={img} 
                            alt={`Thumbnail ${index + 1}`}
                            className="w-full h-full object-cover"
                          />
                        </button>
                      ))}
                    </div>
                  )}
                </div>

                {/* Quick Info */}
                <div className="grid grid-cols-2 gap-4 text-center">
                  <div className="space-y-1">
                    <Clock className="w-4 h-4 text-gray-500 mx-auto" />
                    <span className="text-base text-gray-600">{experience.duration}</span>
                  </div>
                  <div className="space-y-1">
                    <Users className="w-4 h-4 text-gray-500 mx-auto" />
                    <span className="text-base text-gray-600">{experience.groupSize}</span>
                  </div>
                </div>

                {/* Highlights */}
                <div>
                  <h4 className="text-base font-medium text-gray-900 mb-3">Was dich erwartet</h4>
                  <ul className="space-y-2">
                    {experience.details.map((detail, index) => (
                      <li key={index} className="flex items-start space-x-3">
                        <div className="w-1 h-1 bg-gray-400 rounded-full mt-2 flex-shrink-0" />
                        <span className="text-base text-gray-700">{detail}</span>
                      </li>
                    ))}
                  </ul>
                </div>

                {/* Pricing */}
                <div className="pt-4 border-t border-gray-100">
                  <div className="text-center mb-4">
                    <div className="flex items-center justify-center space-x-2 mb-2">
                      <span className="text-2xl font-medium text-gray-900">{experience.price}</span>
                      <span className="text-sm text-gray-500 line-through">{experience.originalPrice}</span>
                    </div>
                    <p className="text-sm text-gray-600">für 2 Personen | {experience.duration}</p>
                  </div>

                  {/* Testimonial */}
                  {experience.testimonial && (
                    <div className="bg-gray-50 p-4 rounded-lg mb-4">
                      <p className="text-sm italic text-gray-700 text-center">"{experience.testimonial}"</p>
                    </div>
                  )}

                  <button className="w-full bg-editorial-900 text-white px-6 py-3 rounded-lg hover:bg-editorial-800 transition-colors text-base font-medium">
                    Jetzt reservieren
                  </button>
                </div>

                {/* Collapse indicator */}
                <div className="flex items-center justify-center pt-2">
                  <ChevronUp className="w-4 h-4 text-gray-400" />
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
};

const ExperiencesShowcase = () => {
  const [expandedCards, setExpandedCards] = useState(new Set());

  const handleToggleCard = (cardId) => {
    setExpandedCards(prev => {
      const newSet = new Set(prev);
      if (newSet.has(cardId)) {
        newSet.delete(cardId);
      } else {
        newSet.add(cardId);
      }
      return newSet;
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto px-6 py-16">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <p className="text-sm text-gray-600 mb-4 tracking-wide uppercase">Exklusive Kurztrip-Pakete 2025</p>
          <h2 className="text-4xl font-light mb-6 text-gray-900">
