import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, Mail } from 'lucide-react';
import axios from 'axios';

const questions = [
  {
    id: 'memory',
    question: 'Was wird euer "Wei√üt du noch...?"-Moment?',
    options: [
      { value: 'romance', label: 'üíï Romance Escapes\nChampagner-Fr√ºhst√ºck im Bett bis die Sonne untergeht' },
      { value: 'foodie', label: 'üçæ Foodie Adventures\nDas 7-G√§nge-Men√º bei Tim Raue, das euer Leben ver√§ndert' },
      { value: 'squad', label: 'üëØ‚Äç‚ôÄÔ∏è Squad Goals\nBis 4 Uhr morgens lachen, bis der Bauch wehtut und die Nachbarn klopfen' },
      { value: 'adrenaline', label: '‚õ∞Ô∏è Adrenaline Rush\nDen Gipfel erobern und die Welt zu euren F√º√üen liegen sehen' },
      { value: 'decadence', label: 'ü•Ç Pure Decadence\nRos√© auf der Yacht oder Champagner im Infinity Pool' },
      { value: 'cultural', label: 'üáÆüáπ Cultural Immersion\nNonnas geheime Pasta-Kunst lernen (und f√ºr immer behalten)' },
      { value: 'wild', label: 'üåä Wild Nights\nBarfu√ü im Sand tanzen, w√§hrend die Sonne aufgeht' },
      { value: 'insider', label: 'üèõÔ∏è Insider Access\nMit dem besten Architekten der Stadt durch Geheimg√§nge schleichen' },
      { value: 'culture', label: 'üé® Mind-Blowing Culture\nVor DIESEM Kunstwerk stehen, das alles ver√§ndert' }
    ]
  },
  {
    id: 'setting',
    question: 'Euer Zuhause auf Zeit?',
    options: [
      { value: 'luxury', label: 'üëë The Ultimate Luxury\nPersonal Butler, goldene Wasserh√§hne, jeder Wunsch wird erf√ºllt bevor ihr ihn ausgesprochen habt' },
      { value: 'authentic', label: 'üèöÔ∏è Authentic Soul\nJahrhundertealte Mauern, Geschichten in jeder Ecke, der Besitzer erz√§hlt beim Wein von seinem Urgro√üvater' },
      { value: 'instagram', label: 'üì∏ Instagram Heaven\nRooftop Pool mit Skyline-View, Designer-M√∂bel, jede Ecke ein perfektes Foto' },
      { value: 'hidden', label: 'üèîÔ∏è Hidden Gem\nNur per Helikopter erreichbar, kein Handynetz, nur ihr und die Natur' }
    ]
  },
  {
    id: 'time_allocation',
    question: '‚è∞ Wie verbringt ihr eure kostbare Zeit?\nVerteilt eure Zeit auf die Dinge, die euch wichtig sind:',
    isTimeAllocation: true,
    options: [
      { value: 'culinary', label: 'ü•ò Culinary Highs', description: 'Michelin-Sterne, Streetfood-Touren, Kochkurse mit Locals' },
      { value: 'suite', label: 'üõèÔ∏è Suite Life', description: 'Room Service, Balkon mit View, einfach mal nichts tun m√ºssen' },
      { value: 'culture', label: 'üé≠ VIP Culture', description: 'Private Museumsf√ºhrungen, Backstage-Access, Einheimischen-Geheimtipps' },
      { value: 'active', label: 'üèÉ‚Äç‚ôÄÔ∏è Active Adventures', description: 'Sunrise Yoga, Epic Hikes, E-Bike durch Weinberge' },
      { value: 'wellness', label: 'üíÜ‚Äç‚ôÄÔ∏è Wellness Therapy', description: 'Spa-Days, Hot Stone Massages, Digital Detox' },
      { value: 'nightlife', label: 'üç∏ Nightlife Legends', description: 'Rooftop-Bars, Underground-Clubs, bis die Sonne aufgeht' },
      { value: 'surprise', label: 'üéÅ Plot Twists', description: 'Lasst uns √ºberraschen - die besten Geschichten entstehen ungeplant' },
      { value: 'wandering', label: 'üö∂‚Äç‚ôÄÔ∏è Urban Wandering', description: 'Durch Gassen schlendern, spontan in Caf√©s verschwinden, die Stadt auf euch wirken lassen' }
    ]
  }
];

const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [email, setEmail] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [timeAllocations, setTimeAllocations] = useState({});

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));

    if (currentQuestion < questions.length - 1) {
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    } else {
      // After the last question, move to email form
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    }
  };

  const handleTimeAllocation = (optionValue, value) => {
    const newValue = Math.max(0, Math.min(100, parseInt(value) || 0));

    // Calculate current total excluding the current option
    const currentTotal = Object.entries(timeAllocations)
      .filter(([key]) => key !== optionValue)
      .reduce((sum, [, val]) => sum + val, 0);

    // Calculate maximum allowed value for this option
    const maxAllowed = 100 - currentTotal;
    const finalValue = Math.min(newValue, maxAllowed);

    setTimeAllocations(prev => ({
      ...prev,
      [optionValue]: finalValue
    }));
  };

  const getTotalTimeAllocated = () => {
    return Object.values(timeAllocations).reduce((sum, val) => sum + val, 0);
  };

  const getRemainingTime = () => {
    return Math.max(0, 100 - getTotalTimeAllocated());
  };

  const canProceedFromTimeAllocation = () => {
    return getTotalTimeAllocated() === 100;
  };

  const resetTimeAllocations = () => {
    setTimeAllocations({});
  };


  const goBack = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await axios.post('http://localhost:5001/api/quiz', {
        answers,
        timeAllocations,
        email
      });
      setIsSubmitted(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Es gab einen Fehler beim Absenden. Bitte versuchen Sie es erneut.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetQuiz = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setEmail('');
    setIsSubmitted(false);
    setTimeAllocations({});
  };

  if (isSubmitted) {
    return (
      <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
        <div className="container-editorial max-w-4xl text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="w-24 h-24 bg-sage-100 rounded-none flex items-center justify-center mx-auto mb-8">
              <CheckCircle className="w-12 h-12 text-sage-600" />
            </div>
            <h2 className="text-section mb-6 text-editorial-900">
              Danke! Wir schicken euch schon bald die besten Kurztrips f√ºr euer Gef√ºhl.
            </h2>
            <p className="text-body mb-8 max-w-2xl mx-auto text-editorial-700">
              Ihr werdet eine E-Mail mit euren personalisierten Reiseempfehlungen erhalten.
            </p>
            <button 
              onClick={resetQuiz}
              className="btn-secondary"
            >
              Quiz erneut starten
            </button>
          </motion.div>
        </div>
      </section>
    );
  }

  return (
    <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
      <div className="container-editorial max-w-4xl">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="section-badge mb-6">
            <div className="section-badge-dot"></div>
            <p className="text-caption">Unser Gesp√ºr</p>
          </div>
          <h2 className="text-section mb-6 text-editorial-900">
            Euer perfekter Lifestyle-Trip wartet
          </h2>
          <p className="text-body max-w-2xl mx-auto text-editorial-700 mb-8">
            Nach Jahren im Gesch√§ft wissen wir: Es braucht nur 3 richtige Fragen, um euren Traumtrip zu finden. W√§hrend andere euch 50 Filter vorsetzen, haben wir es auf das Wesentliche reduziert. Quiz ausf√ºllen, perfekte Empfehlung bekommen ‚Äì so einfach ist das.
          </p>
        </motion.div>

        <div className="card p-12">
          {/* Progress bar */}
          <div className="mb-12">
            <div className="flex justify-between text-caption text-editorial-500 mb-4">
              <span>Frage {currentQuestion + 1} von {questions.length}</span>
              <span>{Math.round(((currentQuestion + 1) / questions.length) * 100)}%</span>
            </div>
            <div className="w-full bg-sage-200 rounded-none h-1">
              <motion.div 
                className="bg-coral-500 h-1 rounded-none"
                initial={{ width: 0 }}
                animate={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                transition={{ duration: 0.3 }}
              />
            </div>
          </div>

          <AnimatePresence mode="wait">
            {currentQuestion < questions.length ? (
              <motion.div
                key={currentQuestion}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                {currentQuestion > 0 && (
                  <div className="mb-8">
                    <button
                      onClick={goBack}
                      className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                      </svg>
                      Zur√ºck
                    </button>
                  </div>
                )}

                <h3 className="text-2xl font-medium mb-8 text-left text-editorial-900 whitespace-pre-line">
                  {questions[currentQuestion].question}
                </h3>

                {questions[currentQuestion].isTimeAllocation ? (
                  <div className="space-y-6">
                    {/* Time Budget Header */}
                    <div className="bg-gradient-to-r from-sage-50 to-coral-50 rounded-xl p-6 border-2 border-sage-200">
                      <div className="flex items-center justify-between mb-4">
                        <h4 className="text-lg font-semibold text-editorial-900">‚è∞ Euer Zeit-Budget</h4>
                        <div className="flex items-center space-x-4">
                          <div className="text-2xl font-bold text-coral-600">
                            {getRemainingTime()}%
                          </div>
                          <span className="text-editorial-600">verbleibend</span>
                        </div>
                      </div>
                      <div className="w-full bg-sage-200 rounded-full h-3">
                        <motion.div 
                          className="bg-gradient-to-r from-coral-400 to-coral-500 h-3 rounded-full"
                          initial={{ width: 0 }}
                          animate={{ width: `${getTotalTimeAllocated()}%` }}
                          transition={{ duration: 0.3 }}
                        />
                      </div>
                      <p className="text-sm text-editorial-600 mt-2">
                        {getTotalTimeAllocated() === 100 ? 'üéâ Perfekt! Ihr habt eure Zeit optimal verteilt.' : 
                         `Verteilt noch ${getRemainingTime()}% eurer Zeit auf die Aktivit√§ten, die euch wichtig sind.`}
                      </p>
                    </div>

                    {/* Quick Action Buttons */}
                    <div className="bg-sage-50 rounded-xl p-4 border border-sage-200">
                      <h5 className="text-sm font-semibold text-editorial-700 mb-3">üöÄ Schnellstart-Optionen:</h5>
                      <div className="flex flex-wrap gap-2">
                        <button
                          onClick={() => {
                            const balanced = {
                              culinary: 12, suite: 12, culture: 12, active: 12,
                              wellness: 12, nightlife: 12, surprise: 12, wandering: 12
                            };
                            setTimeAllocations(balanced);
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          ‚öñÔ∏è Ausgewogen (je 12%)
                        </button>
                        <button
                          onClick={() => {
                            const foodie = {
                              culinary: 40, suite: 15, culture: 15, active: 10,
                              wellness: 10, nightlife: 5, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(foodie);
                          }}
                          className="px-3 py-1 bg-orange-100 hover:bg-orange-200 text-orange-700 text-xs rounded-full transition-colors"
                        >
                          üçΩÔ∏è Foodie-Fokus
                        </button>
                        <button
                          onClick={() => {
                            const luxury = {
                              culinary: 20, suite: 35, culture: 15, active: 5,
                              wellness: 15, nightlife: 5, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(luxury);
                          }}
                          className="px-3 py-1 bg-blue-100 hover:bg-blue-200 text-blue-700 text-xs rounded-full transition-colors"
                        >
                          üëë Luxus-Fokus
                        </button>
                        <button
                          onClick={() => {
                            const adventure = {
                              culinary: 15, suite: 5, culture: 20, active: 35,
                              wellness: 10, nightlife: 10, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(adventure);
                          }}
                          className="px-3 py-1 bg-green-100 hover:bg-green-200 text-green-700 text-xs rounded-full transition-colors"
                        >
                          üèÉ‚Äç‚ôÄÔ∏è Abenteuer-Fokus
                        </button>
                        <button
                          onClick={() => {
                            setTimeAllocations({});
                          }}
                          className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-700 text-xs rounded-full transition-colors"
                        >
                          üîÑ Zur√ºcksetzen
                        </button>
                      </div>
                    </div>

                    {/* Activity Cards with Sliders */}
                    <div className="grid gap-4">
                      {questions[currentQuestion].options.map((option, index) => {
                        const currentValue = timeAllocations[option.value] || 0;
                        const currentTotal = getTotalTimeAllocated();
                        const maxAllowed = 100 - (currentTotal - currentValue);

                        return (
                          <motion.div
                            key={option.value}
                            className="bg-sage-50 border-2 border-sage-200 rounded-xl p-6 transition-all duration-300 hover:shadow-lg hover:border-coral-300"
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: index * 0.1 }}
                          >
                            <div className="flex items-center justify-between mb-4">
                              <div className="flex-1">
                                <h5 className="text-lg font-semibold text-editorial-900 mb-1">
                                  {option.label}
                                </h5>
                                <p className="text-sm text-editorial-600">
                                  {option.description}
                                </p>
                              </div>
                              <div className="flex items-center space-x-3 ml-6">
                                <div className="text-2xl font-bold text-coral-600 min-w-[3rem] text-center">
                                  {currentValue}%
                                </div>
                              </div>
                            </div>

                            {/* Slider */}
                            <div className="relative">
                              <input
                                type="range"
                                min="0"
                                max={maxAllowed}
                                value={currentValue}
                                onChange={(e) => handleTimeAllocation(option.value, e.target.value)}
                                className="w-full h-3 bg-sage-200 rounded-lg appearance-none cursor-pointer slider"
                                style={{
                                  background: `linear-gradient(to right, #f59e0b 0%, #f59e0b ${currentValue}%, #e5e7eb ${currentValue}%, #e5e7eb 100%)`
                                }}
                              />
                              <div className="flex justify-between text-xs text-editorial-500 mt-2">
                                <span>0%</span>
                                <span className="text-coral-600 font-medium">
                                  Max: {maxAllowed}%
                                </span>
                                <span>100%</span>
                              </div>
                            </div>
                          </motion.div>
                        );
                      })}
                    </div>

                    {/* Continue Button */}
                    <motion.button
                      onClick={() => {
                        if (canProceedFromTimeAllocation()) {
                          setAnswers(prev => ({ ...prev, [questions[currentQuestion].id]: timeAllocations }));
                          setTimeout(() => {
                            setCurrentQuestion(prev => prev + 1);
                          }, 300);
                        }
                      }}
                      disabled={!canProceedFromTimeAllocation()}
                      className={`w-full py-4 px-8 rounded-xl font-semibold text-lg transition-all duration-300 ${
                        canProceedFromTimeAllocation()
                          ? 'bg-gradient-to-r from-coral-500 to-coral-600 text-white hover:from-coral-600 hover:to-coral-700 shadow-lg hover:shadow-xl transform hover:scale-105'
                          : 'bg-sage-200 text-sage-500 cursor-not-allowed'
                      }`}
                      whileHover={canProceedFromTimeAllocation() ? { scale: 1.02 } : {}}
                      whileTap={canProceedFromTimeAllocation() ? { scale: 0.98 } : {}}
                    >
                      {canProceedFromTimeAllocation() ? 'üéØ Weiter zu euren Trips!' : `Noch ${getRemainingTime()}% zu verteilen`}
                    </motion.button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {questions[currentQuestion].options.map((option, index) => (
                      <motion.button
                        key={option.value}
                        className="w-full p-6 text-left border-2 border-sage-200 rounded-lg hover:border-coral-400 hover:bg-coral-50 transition-all duration-300 group"
                        onClick={() => handleAnswer(questions[currentQuestion].id, option.value)}
                      >
                        <span className="text-base leading-relaxed text-editorial-700 group-hover:text-editorial-900 transition-colors duration-300 whitespace-pre-line">
                          {option.label}
                        </span>
                      </motion.button>
                    ))}
                  </div>
                )}
              </motion.div>
            ) : (
              <motion.div
                key="email-form"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                <div className="mb-8">
                  <button
                    onClick={goBack}
                    className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                  >
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                    Zur√ºck zu den Fragen
                  </button>
                </div>

                <div className="text-center mb-12">
                  <div className="w-20 h-20 bg-warm-100 rounded-none flex items-center justify-center mx-auto mb-8">
                    <Mail className="w-10 h-10 text-editorial-700" />
                  </div>
                  <h3 className="text-2xl font-medium mb-6 text-editorial-900">
                    Eure drei perfekten Auszeiten warten bereits.
                  </h3>
                  <p className="text-body max-w-2xl mx-auto text-editorial-700">
                    Basierend auf euren Antworten haben wir handverlesene Erlebnisse kuratiert. Tragt eure E-Mail ein und wir schicken sie euch ‚Äì mit allem, was ihr wissen m√ºsst.
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="mb-8">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Eure E-Mail-Adresse"
                      className="w-full p-6 border border-editorial-300 rounded-none focus:outline-none focus:ring-2 focus:ring-editorial-500 focus:border-transparent text-body"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="w-full btn-primary text-lg py-6 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isSubmitting ? 'Wird gesendet...' : 'Trips senden'}
                  </button>
                </form>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default Quiz;
