import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, Mail } from 'lucide-react';
import axios from 'axios';

const questions = [
  {
    id: 'trip_type',
    question: 'Was ist das Wichtigste für euren perfekten Trip?',
    options: [
      { value: 'romance', label: '💕 Zweisamkeit\nZeit füreinander haben, ohne Ablenkung, intensive Gespräche' },
      { value: 'unique', label: '🌟 Etwas Besonderes\nEtwas erleben, was sonst niemand hat - einzigartige Stories sammeln' },
      { value: 'relaxation', label: '🧘‍♀️ Pure Entspannung\nEndlich mal runterkommen, Stress vergessen, Seele baumeln lassen' },
      { value: 'culture', label: '🎭 Kulturelle Erlebnisse\nNeue Welten entdecken, lernen, inspiriert werden' },
      { value: 'adventure', label: '🏔️ Outdoor Abenteuer\nAction, Adrenalin, unvergessliche Kulissen' },
      { value: 'party', label: '🎉 Feierei!!\nParty, Lachen, das Leben feiern' },
      { value: 'instagram', label: '📸 Instagram Moments\nWow-Momente schaffen, die alle neidisch machen' }
    ]
  },
  {
    id: 'accommodation',
    question: 'Wie sieht euer Zuhause auf Zeit aus?',
    options: [
      { value: 'luxury', label: '🛎️ Luxuriös\nInfinity Pool, Designer Möbel und Service, der keinen Wunsch offen lässt' },
      { value: 'charming', label: '🕯️ Charmant\nJahrhundertealte Mauern, Geschichten in jeder Ecke, Wein mit dem Besitzer' },
      { value: 'unique', label: '✨ Außergewöhnlich\nUnter Sternen schlafen, im Kloster meditieren, in Thermen baden - außergewöhnlich' },
      { value: 'remote', label: '🏔️ Abgeschottet\nAbgeschottet in der Natur, ohne Handynetz - nur Zeit für euch' }
    ]
  },
  {
    id: 'time_allocation',
    question: 'Wie möchtet ihr eure Auszeit verbringen?',
    isTimeAllocation: true,
    options: [
      { value: 'culinary', label: '🥘 Kulinarische Verwöhnung' },
      { value: 'suite', label: '🛏️ Suite Life' },
      { value: 'culture', label: '🎭 VIP Kultur' },
      { value: 'active', label: '🏃‍♀️ Abenteuer' },
      { value: 'wellness', label: '💆‍♀️ Wellness' },
      { value: 'nightlife', label: '🍸 Nightlife' },
      { value: 'surprise', label: '🎁 Überraschungen' },
      { value: 'wandering', label: '🚶‍♀️ Stadt erkunden' }
    ]
  }
];

const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [email, setEmail] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [timeAllocations, setTimeAllocations] = useState({});

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));

    if (currentQuestion < questions.length - 1) {
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    } else {
      // After the last question, move to email form
      setTimeout(() => {
        setCurrentQuestion(prev => prev + 1);
      }, 300);
    }
  };

  const handleTimeAllocation = (optionValue, value) => {
    const newValue = Math.max(0, Math.min(100, parseInt(value) || 0));

    // Calculate current total excluding the current option
    const currentTotal = Object.entries(timeAllocations)
      .filter(([key]) => key !== optionValue)
      .reduce((sum, [, val]) => sum + val, 0);

    // Calculate maximum allowed value for this option
    const maxAllowed = 100 - currentTotal;
    const finalValue = Math.min(newValue, maxAllowed);

    setTimeAllocations(prev => ({
      ...prev,
      [optionValue]: finalValue
    }));
  };

  const getTotalTimeAllocated = () => {
    return Object.values(timeAllocations).reduce((sum, val) => sum + val, 0);
  };

  const getRemainingTime = () => {
    return Math.max(0, 100 - getTotalTimeAllocated());
  };

  const canProceedFromTimeAllocation = () => {
    return getTotalTimeAllocated() === 100;
  };

  // Reset time allocations (unused but kept for future functionality)
  // const resetTimeAllocations = () => {
  //   setTimeAllocations({});
  // };


  const goBack = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await axios.post('http://localhost:5001/api/quiz', {
        answers,
        timeAllocations,
        email
      });
      setIsSubmitted(true);
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Es gab einen Fehler beim Absenden. Bitte versuchen Sie es erneut.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetQuiz = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setEmail('');
    setIsSubmitted(false);
    setTimeAllocations({});
  };

  if (isSubmitted) {
    return (
      <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
        <div className="container-editorial max-w-4xl text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
          >
            <div className="w-24 h-24 bg-sage-100 rounded-none flex items-center justify-center mx-auto mb-8">
              <CheckCircle className="w-12 h-12 text-sage-600" />
            </div>
            <h2 className="text-section mb-6 text-editorial-900">
              Danke! Wir schicken euch schon bald die besten Kurztrips für euer Gefühl.
            </h2>
            <p className="text-body mb-8 max-w-2xl mx-auto text-editorial-700">
              Ihr werdet eine E-Mail mit euren personalisierten Reiseempfehlungen erhalten.
            </p>
            <button 
              onClick={resetQuiz}
              className="btn-secondary"
            >
              Quiz erneut starten
            </button>
          </motion.div>
        </div>
      </section>
    );
  }

  return (
    <section id="quiz" className="section-padding bg-warm-50 border-b border-warm-200">
      <div className="container-editorial max-w-4xl">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="section-badge mb-6">
            <div className="section-badge-dot"></div>
            <p className="text-caption">Unser Gespür</p>
          </div>
          <h2 className="text-section mb-6 text-editorial-900">
            Euer perfekter Lifestyle-Trip wartet
          </h2>
          <p className="text-body max-w-2xl mx-auto text-editorial-700 mb-8">
            Nach Jahren im Geschäft wissen wir: Es braucht nur 3 richtige Fragen, um euren Traumtrip zu finden. Während andere euch 50 Filter vorsetzen, haben wir es auf das Wesentliche reduziert. Quiz ausfüllen, perfekte Empfehlung bekommen – so einfach ist das.
          </p>
        </motion.div>

        <div className="card p-12">
          {/* Progress bar */}
          <div className="mb-12">
            <div className="flex justify-between text-caption text-editorial-500 mb-4">
              <span>Frage {currentQuestion + 1} von {questions.length}</span>
              <span>{Math.round(((currentQuestion + 1) / questions.length) * 100)}%</span>
            </div>
            <div className="w-full bg-sage-200 rounded-none h-1">
              <motion.div 
                className="bg-coral-500 h-1 rounded-none"
                initial={{ width: 0 }}
                animate={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                transition={{ duration: 0.3 }}
              />
            </div>
          </div>

          <AnimatePresence mode="wait">
            {currentQuestion < questions.length ? (
              <motion.div
                key={currentQuestion}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                {currentQuestion > 0 && (
                  <div className="mb-8">
                    <button
                      onClick={goBack}
                      className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                    >
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                      </svg>
                      Zurück
                    </button>
                  </div>
                )}

                <h3 className="text-2xl font-medium mb-8 text-left text-editorial-900 whitespace-pre-line">
                  {questions[currentQuestion].question}
                </h3>

                {questions[currentQuestion].isTimeAllocation ? (
                  <div className="space-y-6">
                    {/* Time Budget Header */}
                    <div className="bg-gradient-to-r from-sage-50 to-sage-100 rounded-xl p-6 border-2 border-sage-200">
                      <div className="flex items-center justify-between mb-4">
                        <h4 className="text-lg font-semibold text-editorial-900">⏰ Euer Zeit-Budget</h4>
                        <div className="flex items-center space-x-4">
                          <div className="text-2xl font-bold text-sage-600">
                            {getRemainingTime()}%
                          </div>
                          <span className="text-editorial-600">verbleibend</span>
                        </div>
                      </div>
                      <div className="w-full bg-sage-200 rounded-full h-3">
                        <motion.div 
                          className="bg-gradient-to-r from-sage-400 to-sage-500 h-3 rounded-full"
                          initial={{ width: 0 }}
                          animate={{ width: `${getTotalTimeAllocated()}%` }}
                          transition={{ duration: 0.3 }}
                        />
                      </div>
                      <p className="text-sm text-editorial-600 mt-2">
                        {getTotalTimeAllocated() === 100 ? '🎉 Perfekt! Ihr habt eure Zeit optimal verteilt.' : 
                         `Verteilt noch ${getRemainingTime()}% eurer Zeit auf die Aktivitäten, die euch wichtig sind.`}
                      </p>
                    </div>

                    {/* Quick Action Buttons */}
                    <div className="bg-sage-50 rounded-xl p-4 border border-sage-200">
                      <h5 className="text-sm font-semibold text-editorial-700 mb-3">🚀 Schnellstart-Optionen:</h5>
                      <div className="flex flex-wrap gap-2">
                        <button
                          onClick={() => {
                            const balanced = {
                              culinary: 12, suite: 12, culture: 12, active: 12,
                              wellness: 12, nightlife: 12, surprise: 12, wandering: 12
                            };
                            setTimeAllocations(balanced);
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          ⚖️ Ausgewogen
                        </button>
                        <button
                          onClick={() => {
                            const foodie = {
                              culinary: 40, suite: 15, culture: 15, active: 10,
                              wellness: 10, nightlife: 5, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(foodie);
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          🍽️ Foodie-Fokus
                        </button>
                        <button
                          onClick={() => {
                            const luxury = {
                              culinary: 20, suite: 35, culture: 15, active: 5,
                              wellness: 15, nightlife: 5, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(luxury);
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          👑 Luxus-Fokus
                        </button>
                        <button
                          onClick={() => {
                            const adventure = {
                              culinary: 15, suite: 5, culture: 20, active: 35,
                              wellness: 10, nightlife: 10, surprise: 3, wandering: 2
                            };
                            setTimeAllocations(adventure);
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          🏃‍♀️ Abenteuer-Fokus
                        </button>
                        <button
                          onClick={() => {
                            setTimeAllocations({});
                          }}
                          className="px-3 py-1 bg-sage-200 hover:bg-sage-300 text-sage-700 text-xs rounded-full transition-colors"
                        >
                          🔄 Zurücksetzen
                        </button>
                      </div>
                    </div>

                    {/* Clean Activity Cards */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                      {questions[currentQuestion].options.map((option, index) => {
                        const currentValue = timeAllocations[option.value] || 0;
                        const currentTotal = getTotalTimeAllocated();
                        // const maxAllowed = 100 - (currentTotal - currentValue);

                        return (
                          <motion.div
                            key={option.value}
                            className={`bg-sage-50 border-2 rounded-lg p-4 transition-all duration-300 hover:shadow-md ${
                              currentValue > 0 ? 'border-sage-400 bg-sage-100' : 'border-sage-200'
                            }`}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: index * 0.05 }}
                          >
                            <div className="text-center mb-3">
                              <h5 className="text-sm font-semibold text-editorial-900 mb-2">
                                {option.label}
                              </h5>
                              <div className="text-2xl font-bold text-sage-600">
                                {currentValue}%
                              </div>
                            </div>

                            {/* Simple Slider */}
                            <div className="space-y-2">
                              <input
                                type="range"
                                min="0"
                                max="100"
                                value={currentValue}
                                onChange={(e) => handleTimeAllocation(option.value, e.target.value)}
                                className="w-full h-2 bg-sage-200 rounded-lg appearance-none cursor-pointer slider"
                                style={{
                                  background: `linear-gradient(to right, #f59e0b 0%, #f59e0b ${currentValue}%, #e5e7eb ${currentValue}%, #e5e7eb 100%)`
                                }}
                              />
                            </div>
                          </motion.div>
                        );
                      })}
                    </div>

                    {/* Continue Button */}
                    <div className="mt-8 flex justify-center">
                      <motion.button
                      onClick={() => {
                        if (canProceedFromTimeAllocation()) {
                          setAnswers(prev => ({ ...prev, [questions[currentQuestion].id]: timeAllocations }));
                          setTimeout(() => {
                            setCurrentQuestion(prev => prev + 1);
                          }, 300);
                        }
                      }}
                      disabled={!canProceedFromTimeAllocation()}
                      className={`py-4 px-8 rounded-xl font-semibold text-lg transition-all duration-300 ${
                        canProceedFromTimeAllocation()
                          ? 'bg-gradient-to-r from-coral-500 to-coral-600 text-white hover:from-coral-600 hover:to-coral-700 shadow-lg hover:shadow-xl transform hover:scale-105'
                          : 'bg-sage-200 text-sage-500 cursor-not-allowed'
                      }`}
                      whileHover={canProceedFromTimeAllocation() ? { scale: 1.02 } : {}}
                      whileTap={canProceedFromTimeAllocation() ? { scale: 0.98 } : {}}
                    >
                      <div className="flex items-center justify-center space-x-2">
                        <span>Empfehlungen erhalten</span>
                      </div>
                      </motion.button>
                    </div>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {questions[currentQuestion].options.map((option, index) => (
                      <motion.button
                        key={option.value}
                        className="w-full p-6 text-left border-2 border-sage-200 rounded-lg hover:border-sage-400 hover:bg-sage-100 transition-all duration-300 group"
                        onClick={() => handleAnswer(questions[currentQuestion].id, option.value)}
                      >
                        <span className="text-base leading-relaxed text-editorial-700 group-hover:text-editorial-900 transition-colors duration-300 whitespace-pre-line">
                          {option.label}
                        </span>
                      </motion.button>
                    ))}
                  </div>
                )}
              </motion.div>
            ) : (
              <motion.div
                key="email-form"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Back button */}
                <div className="mb-8">
                  <button
                    onClick={goBack}
                    className="flex items-center text-editorial-600 hover:text-editorial-900 transition-colors duration-300"
                  >
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                    Zurück zu den Fragen
                  </button>
                </div>

                <div className="text-center mb-12">
                  <div className="w-20 h-20 bg-warm-100 rounded-none flex items-center justify-center mx-auto mb-8">
                    <Mail className="w-10 h-10 text-editorial-700" />
                  </div>
                  <h3 className="text-2xl font-medium mb-6 text-editorial-900">
                    Eure drei perfekten Auszeiten warten bereits.
                  </h3>
                  <p className="text-body max-w-2xl mx-auto text-editorial-700">
                    Basierend auf euren Antworten haben wir handverlesene Erlebnisse kuratiert. Tragt eure E-Mail ein und wir schicken sie euch – mit allem, was ihr wissen müsst.
                  </p>
                </div>

                <form onSubmit={handleSubmit}>
                  <div className="mb-8">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Eure E-Mail-Adresse"
                      className="w-full p-6 border border-editorial-300 rounded-none focus:outline-none focus:ring-2 focus:ring-editorial-500 focus:border-transparent text-body"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className="w-full btn-primary text-lg py-6 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isSubmitting ? 'Wird gesendet...' : 'Trips senden'}
                  </button>
                </form>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
};

export default Quiz;
